window.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector("#commentform");if(e)if(window.IntersectionObserver)new IntersectionObserver((function(e){if(e.some((e=>e.isIntersecting))){const e=Date.now();WP_Enqueue_Dynamic_Script.loadScript("verbum").then((()=>{const n=Date.now();VerbumComments.fullyLoadedTime=n-e})),this.disconnect()}})).observe(e);else WP_Enqueue_Dynamic_Script.loadScript("verbum")}));